const assert = require('assert');
const ValidatorPizzaClient = require('../src/index.js');

const client = new ValidatorPizzaClient();

describe('Validator Pizza', () => {
  describe('check if Domain is valid or not', () => {
    it('should return true', () => {
      client.validate('domain', 'yopmail.com').then(response => assert.equal(response.valid, true));
    });

    it('should return false', () => {
      client.validate('domain', 'does-not-exist.testing').then(response => assert.equal(response.valid, false));
    });
  });
});